LinearY<-gtemp t<-time(gtemp) fit1=lm(Y~t) summary(fit1) anova(fit1)Plot original series and fitted valuesplot(gtemp,type="o",xlab="Time (year)", ylab="Temp deviations", main="Global Temperatures and Linear Fit", col="darkgreen") abline(fit1,col="red",lwd=2)Plot residuals obtained from detrended dataplot.ts(fit1$residuals,col="blue",main="Detrended Series", ylab="Temp Deviations -Linear Trend")Regression Diagnostics (Residuals are analysed for regression assumptions)par(mfrow = c(2, 2)) plot(fit1, main="Fit 1 Diagnostics",which = 1:4)R code:install.packages("astsa")library(astsa)Y=gtempt=time(gtemp)tsq=t^2/factorial(2)fit2=lm(Y~t+tsq)plot(gtemp,type="o",xlab="Time(year)")par(new=T)plot(fit2$fitted,col="red",lwd=2,type="l",ylab="",xlab="",main="",xaxt="n",yaxt="n")n=length(Y)aci2=AIC(fit2)/nk=3sse=sum(resid(fit2)^2)aicc2=log(sse/n)+((n+k)/(n-k-2))bic2=(AIC(fit2,k=log(n)))/nanova(fit2)Future:new=data.frame(ts=c(2000:2009))X.true=Y[(values+1):length(t)]fit1=lm(X~ts)Xhat=predict(fit1,new,se.fit=TRUE)X.hat=roundForecast Evaluation CriteriaLinear:Y<-gtempt<-time(gtemp)values<-length(t)-10ts<-t[1:values]X<-Y[1:values]new<-data.frame(ts=c(2000:2009))X.true<-Y[(values+1):length(t)]fit1<-lm(X~ts)Xhat<-predict(fit1,new,se.fit=TRUE)X.hat<-round(Xhat$fit,2)error<-X.true-X.hatme=mean(error)mempe=100*(mean(error/X.true))mse=sum(error^2)/length(error)mae=mean(abs(error)) mape=100*(mean(abs((error)/X.true)))Quadratictsq<-(ts^2)/factorial(2)fit2<-lm(X~ts+tsq)tsn<-2000:2009tsnq<-(tsn^2)/factorial(2)mat<-cbind(tsn,tsnq)new2<-data.frame(mat)Xhat<-predict(fit2,new2,se.fit=TRUE)X.hat<-round(Xhat$fit,2)error<-X.true-X.hatme=mean(error)mpe=100*(mean(error/X.true))mse=sum(error^2)/length(error)mae=mean(abs(error))mape=100*(mean(abs((error)/X.true)))s=c(rep(0,100),10*exp(-(1:100)/20)*cos(2*pi*1:100/4))x=ts(s+rnorm(200,0,1))plot(x,main="signal model")s=c(rep(0,100),10*exp(-(1:100)/200)*cos(2*pi*1:100/4))x=